import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { callAPI } from "../utils/CallApi";
import ProductDetails from "./ProductDetails";

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  const getProduct = () => {
    callAPI(`data/products.json`).then((productResults) => {
      setProduct(productResults[id]);
    });
  };

  useEffect(() => {
    getProduct();
  }, []);

  if (!product?.title) return <h1>Loading Product...</h1>;

  return (
    product && (
      <div className="h-screen bg-amazonclone-background">
        <div className="max-w-[1500px] min-w-[1000px] m-auto  bg-orange-400 ">
          <div className="grid grid-cols-10 gap-2">
            {/* left */}
            <div className="col-span-3 rounded m-auto bg-white p-8">
              <img src={`${product.image}`} alt="#" />
            </div>

            {/* middle */}
            <div className="col-span-5 rounded bg-white divide-y divide-gray-400 p-4">
              <div className="mb-3">
                <ProductDetails product={product} ratings={true} />
              </div>
              <div className="text-base xl:text-lg mt-3">
                {product.description}
              </div>
            </div>

            {/* right */}
            <div className="col-span-2 p-4 bg-white">
              <div className="text-xl xl:text-2xl font-semibold">
                {product.price}
              </div>
              <div>{product.oldPrice}</div>
              <div>FREE Returns</div>
              <div>Free Delivery</div>
              <div>In Stock</div>
              <div>
                Quantity:
                <select>
                  <option>1</option>
                  <option>2</option>
                  <option>3</option>
                  <option>4</option>
                </select>
              </div>
              <button>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    )
  );
};

export default ProductPage;
