import React from "react";
import ProductBadge from "./ProductBadge";
import ProductRatings from "./ProductRatings";

const ProductDetails = ({ product, ratings }) => {
  return (
    <div className="mb-1">
      <div className="text-xl xl:text-2xl mb-1 font-semibold">
        {product.title}
      </div>
      <div className="text-sm xl:text-base font-medium mb-1">
        by <span className="text-blue-600">{product.brand}</span>
      </div>
      {ratings && (
        <div className="text-sm xl:text-base mb-1 font-semibold text-blue-600">
          <ProductRatings
            avgRating={product.avgRating}
            ratings={product.ratings}
          />
        </div>
      )}

      <div className="text-xs xl:text-sm font-bold mb-1">
        {product.attribute}
      </div>
      <div>
        <ProductBadge badge={product.badge} />
      </div>
    </div>
  );
};

export default ProductDetails;
