import React from "react";

function ProductDetails({ product, ratings }) {
  return (
    <div className="mb-1">
      <div className="text-xl xl:text-2xl font-medium mb-1">
        {product.title}
      </div>
      <div className="text-sm xl:text-base font-medium mb-1">
        {product.brand}
      </div>
      <div className="text-xl xl:text-2xl font-md mb-1">
        {product.avgRating}
      </div>
      <div>{product.attribute}</div>
      <div>{product.badge}</div>
    </div>
  );
}

export default ProductDetails;
